from collections import Counter

# Read input from stdin
input = sys.stdin.read
data = input().split()

# Number of test cases
t = int(data[0])
index = 1

# Process each test case
results = []
for _ in range(t):
    n = int(data[index])
    k = int(data[index + 1])
    s = data[index + 2]
    index += 3

    # Count the frequency of each character
    char_count = Counter(s)

    # Count the number of characters with odd frequency
    odd_count = sum(1 for count in char_count.values() if count % 2 != 0)

    # Check if it's possible to form a palindrome by removing exactly k characters
    if k == 0:
        if odd_count <= 1:
            results.append("YES")
        else:
            results.append("NO")
    elif k == 1:
        if odd_count == 0:
            results.append("YES")
        elif odd_count == 1 and n % 2 != 0:
            results.append("YES")
        elif odd_count == 2 and n % 2 == 0:
            results.append("YES")
        else:
            results.append("NO")
    elif k > 1:
        if odd_count == 0 and k % 2 == 0:
            results.append("YES")
        elif odd_count == 1 and k % 2 == 1 and n % 2 == 0:
            results.append("YES")
        elif odd_count == 2 and k % 2 == 0 and n % 2 == 0:
            results.append("YES")
        else:
            results.append("NO")

# Write the results to stdout
for result in results:
    print(result)
