import sys
from collections import Counter

def can_form_palindrome(s, k):
    n = len(s)
    char_count = Counter(s)
    odd_count = sum(1 for count in char_count.values() if count % 2 != 0)

    if k == 0:
        return odd_count <= 1
    elif k >= n:
        return True
    else:
        return odd_count <= k + 1

def main():
    input = sys.stdin.read
    data = input().split()

    index = 0
    t = int(data[index])
    index += 1

    results = []
    for _ in range(t):
        n = int(data[index])
        k = int(data[index + 1])
        s = data[index + 2]
        index += 3

        if can_form_palindrome(s, k):
            results.append("YES")
        else:
            results.append("NO")

    for result in results:
        print(result)

if __name__ == "__main__":
    main()

