class Solution:
    def maximumOr(self, nums: List[int], k: int) -> int:
        n = len(nums)
        left_or = [0] * n
        right_or = [0] * n

        for i in range(1, n):
            left_or[i] = left_or[i-1] | nums[i-1]

        for i in range(n-2, -1, -1):
            right_or[i] = right_or[i+1] | nums[i+1]

        max_or = 0
        for i in range(n):
            current_or = left_or[i] | right_or[i]
            if i > 0:
                current_or |= nums[i-1] << k
            if i < n-1:
                current_or |= nums[i+1] << k
            max_or = max(max_or, current_or)

        return max_or
