class Solution:
    def maximumOr(self, nums: List[int], k: int) -> int:
        n = len(nums)
        left_or = [0] * n
        right_or = [0] * n

        for i in range(1, n):
            left_or[i] = left_or[i - 1] | nums[i - 1]

        for i in range(n - 2, -1, -1):
            right_or[i] = right_or[i + 1] | nums[i + 1]

        max_or = 0
        for i in range(n):
            if i == 0:
                max_or = max(max_or, right_or[0])
            elif i == n - 1:
                max_or = max(max_or, left_or[n - 1])
            else:
                max_or = max(max_or, left_or[i] | right_or[i])

        for i in range(n):
            nums[i] <<= k
            max_or = max(max_or, left_or[i] | nums[i] | right_or[i])
            nums[i] >>= k

        return max_or
