import sys

def min_operations_to_divisible_by_k(a, k):
    from collections import Counter
    from math import gcd

    def prime_factors(n):
        i = 2
        factors = []
        while i * i <= n:
            if n % i:
                i += 1
            else:
                n //= i
                factors.append(i)
        if n > 1:
            factors.append(n)
        return factors

    def count_factors(factors):
        factor_count = Counter()
        for factor in factors:
            factor_count[factor] += 1
        return factor_count

    def lcm(a, b):
        return a * b // gcd(a, b)

    total_factors = []
    for num in a:
        total_factors.extend(prime_factors(num))

    target_factors = prime_factors(k)
    target_factor_count = count_factors(target_factors)

    current_factor_count = count_factors(total_factors)

    operations = 0
    for factor, count in target_factor_count.items():
        if factor in current_factor_count and current_factor_count[factor] >= count:
            continue
        for num in a:
            if factor in prime_factors(num):
                operations += 1
                break

    return operations

